   private void userInformation() {
        String ActiveClient = HomePage.ActiveUser; //Struggles
    try {Connection connection = DriverManager.getConnection("jdbc:mysql://sql9.freesqldatabase.com:3306/sql9630816", "sql9630816", "xPzHtpjvSw"); // Establish connection with SQL Database
        // Prepare the SQL statement to retrieve the data
        PreparedStatement statement = connection.prepareStatement("SELECT client_id, firstname, lastname, login, ufn_FormatPhone(phone_number) AS phone_number, email, birthday_month, birthday_day, birthday_year FROM client WHERE client_id = ?");
        statement.setString(1, ActiveClient);
        // Execute the query
        try(ResultSet resultSet = statement.executeQuery()) {

        if (resultSet.next()) {
            // Retrieve the values from the result set
            String firstname = resultSet.getString("firstname");
            String lastname = resultSet.getString("lastname");
            String name = firstname + ' ' + lastname;
            
            String login = resultSet.getString("login");
           
            String phoneNumber = resultSet.getString("phone_number");
            
            String email = resultSet.getString("email");
            
            String birthdayMonth = resultSet.getString("birthday_month");
            String birthdayDay = resultSet.getString("birthday_day");
            String birthdayYear = resultSet.getString("birthday_year");
            String dateofBirth = birthdayMonth + " " + birthdayDay + ", " + birthdayYear;


            // Populate the text fields with the retrieved values

            txtFullName.setText(name);
            txtUsername.setText(login);
            txtPhoneNum.setText(phoneNumber);
            txtEmail.setText(email);
            txtBirthDate.setText(dateofBirth);

                if (resultSet != null) {
                    resultSet.close();
                }
                if (statement != null) {
                    statement.close();
                }
                if (connection != null) {
                    connection.close();
                }
            }
        } catch (SQLException e) {
            e.printStackTrace();
        }
    } catch (SQLException e) {
        e.printStackTrace();
    }
}

